using System;


namespace projetox
{
    class JoguinhoMarotocopia
    {
        public static void temporario()
        {
            string tecla;
            do
            {
                Console.Clear();
                Console.WriteLine("Escolha uma das opções a baixo:");
                Console.WriteLine("jogar (j)");
                Console.WriteLine("opcoes (o)");
                Console.WriteLine("sair (s)");

                tecla = Console.ReadLine();
                switch (tecla)
                {
                    case "j":
                        jogando();
                        break;
                    case "o":
                        Console.WriteLine();
                        break;
                }
            } while (tecla != "s");
        }
